#!/usr/bin/env python

template_dict = {
    "TPL_ANACONDA_TOKEN": "",
    "TPL_GITHUB_TOKEN": "",
    "TPL_PYPI_PASSWORD": "",
    "PROJECT_NAME": "pycydemo",
    "GITHUB_REPO_NAME": "theochem/python-cython-ci-example",
    }

import sys
from string import Template

template_fn = sys.argv[1]

with open(template_fn) as fh:
    l = fh.read()

t = Template(l)
s = t.safe_substitute(**template_dict)
with open(".travis.yml", "w") as fh:
    fh.write(
        "#\n# THIS FILE IS AUTOGENERATED. DO NOT EDIT DIRECTLY. CHANGES IN \".travis.yml.tpl\" INSTEAD.\n#\n")
    fh.write(s)
